import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import GameSelection from './components/GameSelection';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const GameContainer = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  font-family: 'Press Start 2P', cursive;
`;

const GameCanvas = styled.div`
  width: 800px;
  height: 600px;
  background: rgba(255, 255, 255, 0.1);
  position: relative;
  border-radius: 16px;
  box-shadow: 0 0 30px rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(10px);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  animation: ${fadeIn} 0.5s ease-out;
  overflow-y: auto;
`;

const Title = styled.h1`
  color: #fff;
  font-size: 2.5rem;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
  margin-bottom: 2rem;
  text-align: center;
`;

const GameMenu = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 100%;
  max-width: 400px;
`;

const MenuButton = styled.button`
  background: linear-gradient(45deg, #ff6b6b, #ff8e8e);
  border: none;
  padding: 1rem 2rem;
  color: white;
  font-size: 1.2rem;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-transform: uppercase;
  font-weight: bold;
  box-shadow: 0 4px 15px rgba(255, 107, 107, 0.3);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(255, 107, 107, 0.4);
  }

  &:active {
    transform: translateY(1px);
  }
`;

const PlayerCount = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const PlayerButton = styled(MenuButton)<{ selected?: boolean }>`
  background: ${props => props.selected ? '#4CAF50' : 'linear-gradient(45deg, #4a90e2, #63b3ed)'};
  flex: 1;
`;

const App: React.FC = () => {
  const [selectedPlayers, setSelectedPlayers] = useState<number>(2);
  const [showGameSelection, setShowGameSelection] = useState<boolean>(false);
  const [selectedGame, setSelectedGame] = useState<number | null>(null);

  const handleGameSelect = (gameId: number) => {
    setSelectedGame(gameId);
    // Burada seçilen oyunu başlatma mantığı eklenecek
    console.log(`Seçilen oyun: ${gameId}`);
  };

  return (
    <GameContainer>
      <GameCanvas>
        <Title>2-4 Kişilik Oyunlar</Title>
        {!showGameSelection ? (
          <GameMenu>
            <MenuButton onClick={() => setShowGameSelection(true)}>Oyun Seç</MenuButton>
            <MenuButton>Ayarlar</MenuButton>
            <MenuButton>Nasıl Oynanır?</MenuButton>

            <PlayerCount>
              <PlayerButton 
                selected={selectedPlayers === 2}
                onClick={() => setSelectedPlayers(2)}
              >
                2 Kişi
              </PlayerButton>
              <PlayerButton 
                selected={selectedPlayers === 3}
                onClick={() => setSelectedPlayers(3)}
              >
                3 Kişi
              </PlayerButton>
              <PlayerButton 
                selected={selectedPlayers === 4}
                onClick={() => setSelectedPlayers(4)}
              >
                4 Kişi
              </PlayerButton>
            </PlayerCount>
          </GameMenu>
        ) : (
          <>
            <MenuButton 
              onClick={() => setShowGameSelection(false)}
              style={{ marginBottom: '2rem' }}
            >
              Geri Dön
            </MenuButton>
            <GameSelection 
              selectedPlayers={selectedPlayers}
              onGameSelect={handleGameSelect}
            />
          </>
        )}
      </GameCanvas>
    </GameContainer>
  );
};

export default App;
