// Oyuncu renkleri
const PLAYER_COLORS = ['#e74c3c', '#3498db', '#2ecc71', '#f1c40f'];

interface Player {
  id: number;
  score: number;
  correctAnswers: number;
  totalAnswers: number;
}

interface Question {
  emoji: string;
  correctAnswer: string;
  options: string[];
}

const EmojiGuess: React.FC<{ playerCount: number }> = ({ playerCount }) => {
  // Oyuncu durumlarƒ±
  const [players, setPlayers] = useState<Player[]>(
    Array.from({ length: playerCount }, (_, i) => ({
      id: i,
      score: 0,
      correctAnswers: 0,
      totalAnswers: 0
    }))
  );
  const [activePlayer, setActivePlayer] = useState(0);

  // Oyun durumlarƒ±
  const [phase, setPhase] = useState<'ready' | 'question' | 'result' | 'end'>('ready');
  const [round, setRound] = useState(1);
  const [currentQuestion, setCurrentQuestion] = useState<Question | null>(null);
  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [isCorrect, setIsCorrect] = useState<boolean | null>(null);
  const [usedEmojis, setUsedEmojis] = useState<string[]>([]);

  const totalRounds = playerCount * 5; // Her oyuncu 5 soru cevaplasƒ±n

  // Yeni bir soru olu≈ütur
  const generateQuestion = () => {
    // Kullanƒ±lmamƒ±≈ü bir emoji se√ß
    const availableEmojis = EMOJI_DATA.filter(item => !usedEmojis.includes(item.emoji));

    // T√ºm emojiler kullanƒ±ldƒ±ysa, listeyi sƒ±fƒ±rla
    if (availableEmojis.length === 0) {
      setUsedEmojis([]);
      return generateQuestion();
    }

    // Rastgele emoji se√ß
    const randomIndex = Math.floor(Math.random() * availableEmojis.length);
    const emojiData = availableEmojis[randomIndex];

    // Se√ßilen emojiyi kullanƒ±lmƒ±≈ü olarak i≈üaretle
    setUsedEmojis(prev => [...prev, emojiData.emoji]);

    // Se√ßenekleri karƒ±≈ütƒ±r
    const shuffledOptions = [...emojiData.options].sort(() => Math.random() - 0.5);

    setCurrentQuestion({
      emoji: emojiData.emoji,
      correctAnswer: emojiData.meaning,
      options: shuffledOptions
    });

    setSelectedOption(null);
    setIsCorrect(null);
  };

  // Soru ba≈ülat
  const startQuestion = () => {
    generateQuestion();
    setPhase('question');
  };

  // Se√ßenek se√ßildiƒüinde
  const handleOptionSelect = (option: string) => {
    if (selectedOption || !currentQuestion) return;

    setSelectedOption(option);
    const correct = option === currentQuestion.correctAnswer;
    setIsCorrect(correct);

    // Oyuncu puanƒ±nƒ± g√ºncelle
    setPlayers(prev => prev.map((p, i) => {
      if (i === activePlayer) {
        return { 
          ...p, 
          score: p.score + (correct ? 10 : 0),
          correctAnswers: p.correctAnswers + (correct ? 1 : 0),
          totalAnswers: p.totalAnswers + 1
        };
      }
      return p;
    }));

    // Sonu√ß a≈üamasƒ±na ge√ß
    setTimeout(() => {
      setPhase('result');
    }, 1500);
  };

  // Sonraki a≈üamaya ge√ß
  const nextStep = () => {
    const newRound = round + 1;
    setRound(newRound);

    if (newRound > totalRounds) {
      // Oyun bitti
      setPhase('end');
    } else {
      // Sƒ±radaki oyuncuya ge√ß
      setActivePlayer((activePlayer + 1) % playerCount);
      setPhase('ready');
    }
  };

  // Kazananƒ± bul
  const getWinner = () => {
    return players.reduce((highest, player) => 
      player.score > highest.score ? player : highest, players[0]);
  };

  // Beraberlik kontrol√º
  const isTie = () => {
    const winner = getWinner();
    return players.filter(p => p.score === winner.score).length > 1;
  };

  // Doƒüruluk oranƒ± hesapla
  const getAccuracy = (player: Player) => {
    if (player.totalAnswers === 0) return 0;
    return Math.round((player.correctAnswers / player.totalAnswers) * 100);
  };

  return (
    <GameContainer>
      <Header>Emoji Tahmini</Header>

      <ScoreBoard>
        {players.map((player, index) => (
          <PlayerScore 
            key={player.id} 
            color={PLAYER_COLORS[index]} 
            active={activePlayer === index && phase !== 'end'}
          >
            Oyuncu {index + 1}: {player.score}
          </PlayerScore>
        ))}
      </ScoreBoard>

      {phase === 'ready' && (
        <>
          <InfoText>Sƒ±ra: Oyuncu {activePlayer + 1}</InfoText>
          <p style={{ color: 'white', textAlign: 'center' }}>
            Emoji g√∂sterilecek, doƒüru anlamƒ± se√ßmen gerekiyor.
            Doƒüru cevap verirsen 10 puan kazanƒ±rsƒ±n!
          </p>
          <NextButton onClick={startQuestion}>Hazƒ±rƒ±m</NextButton>
        </>
      )}

      {phase === 'question' && currentQuestion && (
        <>
          <InfoText>Emojinin anlamƒ±nƒ± se√ß!</InfoText>

          <EmojiCard>
            {currentQuestion.emoji}
          </EmojiCard>

          <OptionsContainer>
            {currentQuestion.options.map((option, index) => (
              <OptionButton 
                key={index}
                onClick={() => handleOptionSelect(option)}
                selected={selectedOption === option}
                correct={selectedOption === option && isCorrect === true}
                wrong={selectedOption === option && isCorrect === false}
                disabled={selectedOption !== null}
              >
                {option}
              </OptionButton>
            ))}
          </OptionsContainer>

          {selectedOption && (
            <FeedbackText correct={isCorrect || false}>
              {isCorrect ? '‚úì Doƒüru!' : '‚úó Yanlƒ±≈ü!'}
            </FeedbackText>
          )}
        </>
      )}

      {phase === 'result' && currentQuestion && (
        <>
          <InfoText>
            {isCorrect 
              ? 'Tebrikler! 10 puan kazandƒ±n!' 
              : 'Yanlƒ±≈ü cevap, doƒüru yanƒ±t:'}
          </InfoText>

          <EmojiCard>
            {currentQuestion.emoji}
          </EmojiCard>

          {!isCorrect && (
            <div style={{ 
              color: '#2ecc71', 
              fontWeight: 'bold', 
              fontSize: '1.5rem',
              marginTop: '1rem',
              textAlign: 'center'
            }}>
              {currentQuestion.correctAnswer}
            </div>
          )}

          <NextButton onClick={nextStep}>
            {round >= totalRounds ? 'Sonu√ßlarƒ± G√∂r' : 'Sonraki Soru'}
          </NextButton>
        </>
      )}

      {phase === 'end' && (
        <>
          <InfoText>
            {isTie() 
              ? 'Oyun Bitti! Beraberlik!' 
              : `Oyun Bitti! Kazanan: Oyuncu ${getWinner().id + 1}`
            }
          </InfoText>

          <div style={{ width: '100%', maxWidth: '500px', margin: '1rem auto' }}>
            {players.map((player, index) => (
              <div key={player.id} 
                style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  margin: '1rem 0', 
                  padding: '10px',
                  background: player.id === getWinner().id && !isTie() 
                    ? 'rgba(255,255,255,0.1)' 
                    : 'transparent',
                  borderRadius: '8px'
                }}>
                <div style={{ color: PLAYER_COLORS[index] }}>
                  Oyuncu {player.id + 1}
                </div>
                <div style={{ color: 'white' }}>
                  {player.score} puan 
                  <span style={{ fontSize: '0.8rem', marginLeft: '8px', opacity: 0.8 }}>
                    ({getAccuracy(player)}% doƒüru)
                  </span>
                </div>
              </div>
            ))}
          </div>

          <NextButton onClick={() => window.location.reload()}>
            Tekrar Oyna
          </NextButton>
        </>
      )}
    </GameContainer>
  );
};

export default EmojiGuess;
