import React, { useState, useEffect, useCallback } from 'react';
import styled, { keyframes } from 'styled-components';
import Tank from './Tank';

const GameArea = styled.div`
  width: 100vw;
  height: 100vh;
  background: #d2b48c; // Ã‡Ã¶l rengi
  position: relative;
  overflow: hidden;
`;

const GameGrid = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(0, 0, 0, 0.1) 1px, transparent 1px),
    linear-gradient(90deg, rgba(0, 0, 0, 0.1) 1px, transparent 1px);
  background-size: 40px 40px;
  pointer-events: none;
`;

const Grass = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M50 0 L100 50 L50 100 L0 50 Z' fill='%232ecc71' fill-opacity='0.1'/%3E%3C/svg%3E");
  background-size: 20px 20px;
  pointer-events: none;
`;

const Bullet = styled.div<{ rotation: number }>`
  position: absolute;
  width: 8px;
  height: 8px;
  background: #fff;
  border-radius: 50%;
  transform: rotate(${props => props.rotation}deg);
  box-shadow: 0 0 5px rgba(255, 255, 255, 0.8);
`;

const HealthBar = styled.div<{ health: number; color: string }>`
  position: absolute;
  width: 40px;
  height: 4px;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 2px;
  overflow: hidden;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: ${props => props.health}%;
    height: 100%;
    background: ${props => props.color};
    transition: width 0.3s ease;
  }
`;

const Controls = styled.div`
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 20px;
  z-index: 1;
`;

const ControlButton = styled.button<{ color: string }>`
  background: ${props => props.color};
  border: none;
  width: 50px;
  height: 50px;
  border-radius: 25px;
  color: white;
  font-size: 24px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
  }

  &:active {
    transform: translateY(1px);
  }
`;

const FireButton = styled(ControlButton)`
  background: #e74c3c;
  width: 60px;
  height: 60px;
  font-size: 20px;
`;

const CornerButton = styled.button<{ color: string; position: string }>`
  position: absolute;
  width: 90px;
  height: 90px;
  border-radius: 50%;
  background: ${props => props.color};
  color: #fff;
  font-size: 2.5rem;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 16px rgba(0,0,0,0.15);
  z-index: 10;
  cursor: pointer;
  opacity: 0.95;
  ${props => props.position === 'bottomLeft' && 'left: 30px; bottom: 30px;'}
  ${props => props.position === 'bottomRight' && 'right: 30px; bottom: 30px;'}
  ${props => props.position === 'topLeft' && 'left: 30px; top: 30px;'}
  ${props => props.position === 'topRight' && 'right: 30px; top: 30px;'}
`;

const playerColors: Record<1 | 2 | 3 | 4, string> = {
  1: '#e74c3c', // KÄ±rmÄ±zÄ±
  2: '#3498db', // Mavi
  3: '#2ecc71', // YeÅŸil
  4: '#f1c40f'  // SarÄ±
};

interface Player {
  id: number;
  position: { x: number; y: number };
  rotation: number;
  health: number;
  isMoving: boolean;
}

interface Bullet {
  id: number;
  playerId: number;
  position: { x: number; y: number };
  rotation: number;
}

const cornerPositions = [
  'bottomLeft', // 1. oyuncu
  'bottomRight', // 2. oyuncu
  'topLeft', // 3. oyuncu
  'topRight' // 4. oyuncu
] as const;

type CornerPosition = typeof cornerPositions[number];

const TANK_SIZE = 60;
const CANNON_LENGTH = 30;

const TankGame: React.FC<{ 
  playerCount: number;
  deviceType: 'computer' | 'phone';
}> = ({ playerCount, deviceType }) => {
  const [players, setPlayers] = useState<Player[]>([]);
  const [bullets, setBullets] = useState<Bullet[]>([]);
  const [rotating, setRotating] = useState<{ [id: number]: boolean }>({});
  const [moving, setMoving] = useState<{ [id: number]: boolean }>({});
  const [activePlayer, setActivePlayer] = useState<1 | 2 | 3 | 4>(1);

  useEffect(() => {
    const initialPlayers: Player[] = Array.from({ length: playerCount }, (_, i) => ({
      id: i + 1,
      position: {
        x: window.innerWidth / (playerCount + 1) * (i + 1),
        y: window.innerHeight / 2
      },
      rotation: 0,
      health: 100,
      isMoving: false
    }));
    setPlayers(initialPlayers);
  }, [playerCount]);

  const movePlayer = useCallback((playerId: number, direction: 'up' | 'down' | 'left' | 'right') => {
    setPlayers(prevPlayers => 
      prevPlayers.map(player => {
        if (player.id === playerId) {
          const speed = 5;
          let newX = player.position.x;
          let newY = player.position.y;
          let newRotation = player.rotation;

          switch (direction) {
            case 'up':
              newY -= speed;
              newRotation = 0;
              break;
            case 'down':
              newY += speed;
              newRotation = 180;
              break;
            case 'left':
              newX -= speed;
              newRotation = 270;
              break;
            case 'right':
              newX += speed;
              newRotation = 90;
              break;
          }

          // SÄ±nÄ±rlarÄ± kontrol et
          newX = Math.max(0, Math.min(window.innerWidth - 60, newX));
          newY = Math.max(0, Math.min(window.innerHeight - 60, newY));

          return {
            ...player,
            position: { x: newX, y: newY },
            rotation: newRotation,
            isMoving: true
          };
        }
        return {
          ...player,
          isMoving: false
        };
      })
    );
  }, []);

  const fireBullet = useCallback((playerId: number) => {
    const player = players.find(p => p.id === playerId);
    if (!player) return;

    const rad = player.rotation * Math.PI / 180;
    // TankÄ±n merkezinden namlu ucuna offset
    const offsetX = Math.sin(rad) * (TANK_SIZE / 2 + CANNON_LENGTH / 2);
    const offsetY = -Math.cos(rad) * (TANK_SIZE / 2 + CANNON_LENGTH / 2);
    const bullet: Bullet = {
      id: Date.now() + Math.random(),
      playerId,
      position: {
        x: player.position.x + TANK_SIZE / 2 + offsetX - 4, // 4: mermi yarÄ±Ã§apÄ±
        y: player.position.y + TANK_SIZE / 2 + offsetY - 4
      },
      rotation: player.rotation
    };

    setBullets(prev => [...prev, bullet]);
  }, [players]);

  // Mermi hareketi ve Ã§arpÄ±ÅŸma kontrolÃ¼
  useEffect(() => {
    const bulletInterval = setInterval(() => {
      setBullets(prevBullets => {
        return prevBullets
          .map(bullet => {
            const speed = 10;
            let newX = bullet.position.x + Math.sin(bullet.rotation * Math.PI / 180) * speed;
            let newY = bullet.position.y - Math.cos(bullet.rotation * Math.PI / 180) * speed;
            if (newX < 0 || newX > window.innerWidth || newY < 0 || newY > window.innerHeight) {
              return null;
            }
            return {
              ...bullet,
              position: { x: newX, y: newY }
            };
          })
          .filter((bullet): bullet is Bullet => bullet !== null);
      });
    }, 16);

    return () => clearInterval(bulletInterval);
  }, []);

  // BaÅŸlangÄ±Ã§ta tanklar dÃ¶nÃ¼yor, hareket etmiyor
  useEffect(() => {
    if (deviceType !== 'phone') return;
    const initialRotating: { [id: number]: boolean } = {};
    const initialMoving: { [id: number]: boolean } = {};
    for (let i = 1; i <= playerCount; i++) {
      initialRotating[i] = true;
      initialMoving[i] = false;
    }
    setRotating(initialRotating);
    setMoving(initialMoving);
  }, [playerCount, deviceType]);

  // Tanklar sadece rotating true ise dÃ¶ner
  useEffect(() => {
    if (deviceType !== 'phone') return;
    const interval = setInterval(() => {
      setPlayers(prevPlayers => prevPlayers.map(player => {
        if (rotating[player.id]) {
          return {
            ...player,
            rotation: player.rotation + 2 // SÃ¼rekli artar, sÄ±fÄ±rlanmaz
          };
        }
        return player;
      }));
    }, 30);
    return () => clearInterval(interval);
  }, [rotating, deviceType]);

  // Tanklar sadece moving true ise ileri gider
  useEffect(() => {
    if (deviceType !== 'phone') return;
    const interval = setInterval(() => {
      setPlayers(prevPlayers => prevPlayers.map(player => {
        if (moving[player.id]) {
          const speed = 6;
          let newX = player.position.x;
          let newY = player.position.y;
          let rad = (player.rotation - 90) * Math.PI / 180;
          newX += Math.cos(rad) * speed;
          newY += Math.sin(rad) * speed;
          newX = Math.max(0, Math.min(window.innerWidth - TANK_SIZE, newX));
          newY = Math.max(0, Math.min(window.innerHeight - TANK_SIZE, newY));
          return {
            ...player,
            position: { x: newX, y: newY },
            isMoving: true
          };
        }
        return { ...player, isMoving: false };
      }));
    }, 16);
    return () => clearInterval(interval);
  }, [moving, deviceType]);

  // Butona tÄ±klama: ateÅŸ et (mermi namlunun ucundan, doÄŸru yÃ¶ne Ã§Ä±kacak)
  const handleCornerButtonClick = (playerId: number) => {
    setPlayers(prevPlayers => {
      const player = prevPlayers.find(p => p.id === playerId);
      if (!player) return prevPlayers;
      const rad = (player.rotation - 90) * Math.PI / 180;
      // TankÄ±n merkezinden namlu ucuna offset
      const offsetX = Math.cos(rad) * (TANK_SIZE / 2 + CANNON_LENGTH / 2);
      const offsetY = Math.sin(rad) * (TANK_SIZE / 2 + CANNON_LENGTH / 2);
      const bullet = {
        id: Date.now() + Math.random(),
        playerId,
        position: {
          x: player.position.x + TANK_SIZE / 2 + offsetX - 4, // 4: mermi yarÄ±Ã§apÄ±
          y: player.position.y + TANK_SIZE / 2 + offsetY - 4
        },
        rotation: player.rotation
      };
      setBullets(prev => [...prev, bullet]);
      return prevPlayers;
    });
  };

  // Butona basÄ±lÄ± tutma: hareket baÅŸlar, dÃ¶nme durur
  const handleCornerButtonDown = (playerId: number) => {
    setRotating(r => ({ ...r, [playerId]: false }));
    setMoving(m => ({ ...m, [playerId]: true }));
  };

  // Butondan el Ã§ekilince: hareket durur, dÃ¶nme baÅŸlar
  const handleCornerButtonUp = (playerId: number) => {
    setRotating(r => ({ ...r, [playerId]: true }));
    setMoving(m => ({ ...m, [playerId]: false }));
  };

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (deviceType !== 'computer') return;

      const keyMap: { [key: string]: { playerId: number; direction: 'up' | 'down' | 'left' | 'right' } } = {
        'w': { playerId: 1, direction: 'up' },
        's': { playerId: 1, direction: 'down' },
        'a': { playerId: 1, direction: 'left' },
        'd': { playerId: 1, direction: 'right' },
        'f': { playerId: 2, direction: 'up' },
        'h': { playerId: 2, direction: 'down' },
        'g': { playerId: 2, direction: 'left' },
        't': { playerId: 2, direction: 'right' },
        'i': { playerId: 3, direction: 'up' },
        'k': { playerId: 3, direction: 'down' },
        'j': { playerId: 3, direction: 'left' },
        'l': { playerId: 3, direction: 'right' },
      };

      const action = keyMap[e.key.toLowerCase()];
      if (action) {
        movePlayer(action.playerId, action.direction);
      }

      // AteÅŸ etme tuÅŸlarÄ±
      if (e.key === 'e' && activePlayer === 1) fireBullet(1);
      if (e.key === 'y' && activePlayer === 2) fireBullet(2);
      if (e.key === 'o' && activePlayer === 3) fireBullet(3);
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [deviceType, movePlayer, fireBullet, activePlayer]);

  // Mermiler doÄŸru yÃ¶ne hareket edecek
  useEffect(() => {
    const bulletInterval = setInterval(() => {
      setBullets(prevBullets => {
        return prevBullets
          .map(bullet => {
            const speed = 10;
            const rad = (bullet.rotation - 90) * Math.PI / 180;
            let newX = bullet.position.x + Math.cos(rad) * speed;
            let newY = bullet.position.y + Math.sin(rad) * speed;
            if (newX < 0 || newX > window.innerWidth || newY < 0 || newY > window.innerHeight) {
              return null;
            }
            return {
              ...bullet,
              position: { x: newX, y: newY }
            };
          })
          .filter((bullet): bullet is Bullet => bullet !== null);
      });
    }, 16);
    return () => clearInterval(bulletInterval);
  }, []);

  return (
    <GameArea>
      <GameGrid />
      <Grass />
      {players.map(player => (
        <React.Fragment key={player.id}>
          <HealthBar 
            health={player.health}
            color={playerColors[player.id as 1 | 2 | 3 | 4]}
            style={{
              left: player.position.x + 10,
              top: player.position.y - 10
            }}
          />
          <Tank
            color={playerColors[player.id as 1 | 2 | 3 | 4]}
            rotation={player.rotation}
            position={player.position}
            isMoving={player.isMoving}
          />
        </React.Fragment>
      ))}
      {bullets.map(bullet => (
        <Bullet
          key={bullet.id}
          rotation={bullet.rotation}
          style={{
            left: bullet.position.x,
            top: bullet.position.y
          }}
        />
      ))}
      {deviceType === 'phone' && players.map((player, idx) => (
        <CornerButton
          key={player.id}
          color={playerColors[player.id as 1 | 2 | 3 | 4]}
          position={cornerPositions[idx]}
          onClick={() => handleCornerButtonClick(player.id)}
          onTouchStart={() => handleCornerButtonDown(player.id)}
          onTouchEnd={() => handleCornerButtonUp(player.id)}
          onMouseDown={() => handleCornerButtonDown(player.id)}
          onMouseUp={() => handleCornerButtonUp(player.id)}
          onMouseLeave={() => handleCornerButtonUp(player.id)}
        >
          ðŸš€
        </CornerButton>
      ))}
    </GameArea>
  );
};

export default TankGame;
