interface Player {
  id: number;
  score: number;
}

interface ActiveShape {
  id: number;
  shape: typeof SHAPES[0];
  position: number;
  speed: number;
  caught: boolean;
}

const ShapeDrop: React.FC<{ playerCount: number }> = ({ playerCount }) => {
  const [players, setPlayers] = useState<Player[]>(
    Array.from({ length: playerCount }, (_, i) => ({ id: i, score: 0 }))
  );
  const [activePlayer, setActivePlayer] = useState(0);
  const [phase, setPhase] = useState<'ready' | 'playing' | 'result' | 'end'>('ready');
  const [round, setRound] = useState(0);
  const [targetShape, setTargetShape] = useState<typeof SHAPES[0] | null>(null);
  const [activeShapes, setActiveShapes] = useState<ActiveShape[]>([]);
  const [catcherPosition, setCatcherPosition] = useState(200);
  const [gamePaused, setGamePaused] = useState(false);

  const gameAreaRef = useRef<HTMLDivElement>(null);
  const totalRounds = playerCount * 3; // Her oyuncu 3 tur oynar

  // Yakalama bölgesine tıklama
  const handleGameAreaClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (phase !== 'playing' || gamePaused) return;

    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    setCatcherPosition(Math.max(50, Math.min(350, x)));
  };

  // Turu başlat
  const startRound = () => {
    // Rastgele hedef şekil seç
    const randomShape = SHAPES[Math.floor(Math.random() * SHAPES.length)];
    setTargetShape(randomShape);

    // Aktif şekiller oluştur (3-5 arası)
    const shapeCount = Math.floor(Math.random() * 3) + 3;
    const newShapes: ActiveShape[] = [];

    for (let i = 0; i < shapeCount; i++) {
      // Rastgele bir şekil seç
      const randomIndex = Math.floor(Math.random() * SHAPES.length);
      // Rastgele bir pozisyon belirle (0-340 px arası)
      const randomPosition = Math.floor(Math.random() * 340) + 30;
      // Rastgele bir hız belirle (4-8 saniye)
      const randomSpeed = Math.random() * 4 + 4;

      newShapes.push({
        id: i,
        shape: SHAPES[randomIndex],
        position: randomPosition,
        speed: randomSpeed,
        caught: false
      });
    }

    setActiveShapes(newShapes);
    setCatcherPosition(200);
    setPhase('playing');
  };

  // Şekil düşüşünü kontrol et
  useEffect(() => {
    if (phase !== 'playing' || activeShapes.length === 0) return;

    // En hızlı şekil için zaman belirle
    const fastestShape = activeShapes.reduce(
      (min, shape) => shape.speed < min.speed ? shape : min, 
      activeShapes[0]
    );

    const fallTimeout = setTimeout(() => {
      setGamePaused(true);

      // Yakalamaları kontrol et
      const caughtShapes = activeShapes.filter(shape => {
        const shapeCenterX = shape.position + 30;
        return Math.abs(shapeCenterX - catcherPosition) < 50;
      });

      // Hedef şekili yakalayıp yakalamadığını kontrol et
      const caughtTargetShape = caughtShapes.find(
        shape => shape.shape.name === targetShape?.name
      );

      // Puanı güncelle
      setPlayers(prev => prev.map((p, i) => {
        if (i === activePlayer) {
          let scoreChange = 0;

          if (caughtTargetShape) {
            // Doğru şekli yakaladı
            scoreChange = 10;
          } else if (caughtShapes.length > 0) {
            // Yanlış şekli yakaladı
            scoreChange = -5;
          } else {
            // Hiçbir şekil yakalanmadı
            scoreChange = -3;
          }

          return { ...p, score: Math.max(0, p.score + scoreChange) };
        }
        return p;
      }));

      // Sonuç göster
      setPhase('result');

      // 3 saniye sonra sonraki aşamaya geç
      setTimeout(() => {
        const newRound = round + 1;
        setRound(newRound);

        if (newRound >= totalRounds) {
          setPhase('end');
        } else {
          setActivePlayer((activePlayer + 1) % playerCount);
          setPhase('ready');
          setGamePaused(false);
          setActiveShapes([]);
        }
      }, 3000);
    }, fastestShape.speed * 1000);

    return () => clearTimeout(fallTimeout);
  }, [phase, activeShapes, catcherPosition, round, activePlayer, playerCount, targetShape, totalRounds]);

  // Kazananı bul
  const winner = players.reduce(
    (max, p) => p.score > max.score ? p : max, 
    players[0]
  );

  // Beraberlik durumu
  const isTie = players.filter(p => p.score === winner.score).length > 1;

  return (
    <GameContainer>
      <Header>Şekil Düşürme</Header>

      <ScoreBoard>
        {players.map((player, index) => (
          <PlayerScore 
            key={player.id} 
            color={PLAYER_COLORS[index]} 
            active={activePlayer === index && phase !== 'end'}
          >
            Oyuncu {index + 1}: {player.score}
          </PlayerScore>
        ))}
      </ScoreBoard>

      {phase === 'ready' && (
        <>
          <InfoText>Sıra: Oyuncu {activePlayer + 1}</InfoText>
          <NextButton onClick={startRound}>Hazırım</NextButton>
        </>
      )}

      {(phase === 'playing' || phase === 'result') && targetShape && (
        <>
          <InfoText>
            {phase === 'playing' 
              ? 'Şu şekli yakala:' 
              : gamePaused && activeShapes.some(shape => 
                  shape.shape.name === targetShape.name && 
                  Math.abs((shape.position + 30) - catcherPosition) < 50
                ) 
                ? '✅ Doğru şekli yakaladın!' 
                : activeShapes.some(shape => 
                    Math.abs((shape.position + 30) - catcherPosition) < 50
                  )
                  ? '❌ Yanlış şekil yakaladın!'
                  : '❌ Hiçbir şekil yakalayamadın!'
            }
          </InfoText>

          <TargetShape>
            <div style={{ width: '60px', height: '60px' }}>
              {targetShape.svg}
            </div>
            {targetShape.name}
          </TargetShape>

          <GameArea 
            ref={gameAreaRef}
            onClick={handleGameAreaClick}
          >
            {activeShapes.map(shape => (
              <ShapeContainer 
                key={shape.id}
                position={shape.position}
                animationDuration={shape.speed}
                paused={gamePaused}
                color={
                  phase === 'result' && shape.shape.name === targetShape.name
                    ? '#2ecc71' // Doğru şekil (yeşil)
                    : '#e74c3c'  // Yanlış şekil (kırmızı)
                }
              >
                {shape.shape.svg}
              </ShapeContainer>
            ))}

            <CatcherArea>
              <Catcher 
                position={catcherPosition} 
                color={PLAYER_COLORS[activePlayer]}
              />
            </CatcherArea>
          </GameArea>
        </>
      )}

      {phase === 'end' && (
        <>
          <InfoText>
            {isTie 
              ? 'Oyun Bitti! Beraberlik!' 
              : `Oyun Bitti! Kazanan: Oyuncu ${winner.id + 1}`
            }
          </InfoText>
          <ScoreBoard>
            {players.map((player, index) => (
              <PlayerScore 
                key={player.id} 
                color={PLAYER_COLORS[index]}
                active={player.id === winner.id && !isTie}
              >
                Oyuncu {index + 1}: {player.score}
              </PlayerScore>
            ))}
          </ScoreBoard>
          <NextButton onClick={() => window.location.reload()}>
            Tekrar Oyna
          </NextButton>
        </>
      )}
    </GameContainer>
  );
};

export default ShapeDrop;
